sys_ws_operation.operation_script:
/**
 * POST - Sample Request API - Body
 */
(function process(/*RESTAPIRequest*/ request, /*RESTAPIResponse*/ response) {
    var body = request.body.data;
    //do any additional processing on the request body, such as inserting a new record.

	var needitSysID = '';

	var needit = new GlideRecord('x_58872_needit_needit');
	needit.initialize(); 
	needit.u_requested_for = body.u_requested_for; 
	needit.u_requested_for_email = body.u_requested_for_email; 
	needit.u_request_type = body.u_request_type; 
	needit.u_what_needed = body.u_what_needed; 
	needit.u_when_needed = body.u_when_needed; 
	needit.short_description = body.short_description; 

	//do it	and grab sys_id
	needitSysID = needit.insert();

	var needitLookup = new GlideRecord('x_58872_needit_needit');
	needitLookup.get('sys_id', needitSysID);

	var returnResponse = "NeedIt Task created " + needitLookup.number;
	response.setStatus(201);
	//response.setBody("NeedIt Task created " + needitLookup.number);

	return returnResponse;

})(request, response);
